import React, { ReactNode } from "react";

import cn from "@/helper/cn";

export interface CheckboxProps {
  id?: string;
  name?: string;
  className?: string;
  label?: ReactNode | string;
  disabled?: boolean;
  size?: "sm" | "md" | "lg" | "xl";
  checked: boolean;
  onChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
  theme?: "primary" | "secondary";
}

const sizes = {
  sm: "min-h-4 min-w-4",
  md: "min-h-5 min-w-5",
  lg: "min-h-6 min-w-6",
  xl: "min-h-8 min-w-8",
};

const Checkbox: React.FC<CheckboxProps> = ({
  id = "Checkbox",
  name,
  className,
  label,
  onChange,
  checked,
  disabled,
  size = "lg",
  theme = "primary",
  ...props
}: CheckboxProps) => {
  return (
    <div className="flex items-center cursor-pointer">
      <input
        id={id}
        name={name}
        type="checkbox"
        className={cn([
          sizes[size],
          `peer relative appearance-none border-2 rounded-lg after:absolute after:left-0 after:top-0 after:h-full after:w-full mb-1`,
          disabled &&
            checked &&
            "bg-line-active after:bg-[url('data:image/svg+xml;base64,')] after:bg-[length:40px] after:bg-center after:bg-no-repeat after:content-[''] focus:outline-none",
          !checked &&
            disabled &&
            'bg-line-active after:bg-[length:40px] after:bg-center after:bg-no-repeat after:content-[""] focus:outline-none',
          checked &&
            !disabled &&
            "checked:bg-yellow-500 after:bg-[url('data:image/svg+xml;base64,')] after:bg-[length:40px] after:bg-center after:bg-no-repeat after:content-[''] focus:outline-none",
          checked &&
            !disabled &&
            theme === "secondary" &&
            "checked:bg-yellow-500",
          !disabled &&
            theme !== "secondary" &&
            "cursor-pointer border-yellow-500",
          className,
        ])}
        onChange={onChange}
        checked={checked}
        {...props}
        disabled={disabled}
      />
      <label htmlFor={id} className={cn("ml-3 text-[#696F79] text-base")}>
        {label}
      </label>
    </div>
  );
};

export default Checkbox;
